mapper.select:
SELECT , u.nickname, u.avatar FROM comment c LEFT JOIN user u ON c.user_id = u.id WHERE c.article_id = #{articleId} AND c.deleted = 0
AND c.status = #{status}
ORDER BY c.create_time DESC

SELECT , u.nickname, u.avatar
FROM comment c
LEFT JOIN user u ON c.user_id = u.id
WHERE c.deleted = 0

    AND c.article_id = #{articleId}


    AND c.status = #{status}

ORDER BY c.create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM comment c
WHERE c.deleted = 0

    AND c.article_id = #{articleId}


    AND c.status = #{status}

mapper.update:
UPDATE comment
SET deleted = 1
WHERE id = #{id}
